import { Link } from "react-router-dom";
import { AiOutlineMenu, AiOutlineClose } from "react-icons/ai";
import { useState } from "react";
const NavbarTop = () => {
  const [nav, setNav] = useState(true);
  const handleNav = () => {
    setNav(!nav);
  };

  return (
    <nav className="bg-[#C1E8FF] py-2 px-8 flex justify-between items-center">
      <div className="flex items-center space-x-2">
        <Link to="/" className="text-2xl font-bold text-[#060606]">
          MrBri<span className="text-[#1057E0]">Cool</span>
        </Link>
        <Link
          to="/become-handyman"
          className="text-blue-900 font-bold hover:underline"
        >
          Devenir MrBriCool
        </Link>
      </div>
      <Link
        to="/find-handyman"
        className="text-white bg-[#1057E0] hover:bg-blue-800 outline-none rounded-3xl text-sm px-5 py-2.5 font-bold"
      >
        Trouver un Bricoleur
      </Link>

      {/* Login and Sign Up Buttons */}
      <div className="flex space-x-4">
        <Link
          to="/login"
          className="text-black bg-white hover:bg-gray-100 outline-none rounded-3xl text-sm px-5 py-2.5 font-bold"
        >
          Login
        </Link>
        <Link
          to="/signup"
          className="text-black bg-white hover:bg-gray-100 outline-none rounded-3xl text-sm px-5 py-2.5 font-bold"
        >
          Sign Up
        </Link>
      </div>
    </nav>
  );
};

export default NavbarTop;
