import { useState, useEffect } from "react";

function App() {
  const [nav, setNav] = useState(true);
const handleNav = () => {
  setNav(!nav);
};

const closeNav = () => {
  setNav(true);
};

useEffect(() => {
  const handleResize = () => {
    if (window.innerWidth <= 768) {
      setNav(true);
    }
  };

  window.addEventListener("resize", handleResize);
  return () => {
    window.removeEventListener("resize", handleResize);
  };
}, []);
