import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper.min.css";

export const Slider = ({photo, nameService, description}) => {
   const services = [
      {
        id: 1,
        image: faucet,
        nameService: "Plombier",
        description:
          "Des solutions de plomberie de qualité pour chaque problème de tuyauterie!",
      },
    ];
  return (
    <Swiper spaceBetween={50} slidesPerView={1} align={"center"}>
      <SwiperSlide>
         <img src={photo} alt="servicePhoto" />
         <h3>{nameService}</h3>
         <p>{description}</p>
      </SwiperSlide>
      {services.map((service) => {
        return (
          <section>
            <Slider
              nameService={service.nameService}
              photo={service.image}
              description={service.image}
            />
          </section>
        );
      })}
    </Swiper>
  );
};
