      image: faucet,
      nameService: "Plombier",
      description:
        "Des solutions de plomberie de qualité pour chaque problème de tuyauterie!",
    },
  ];
  return (
    <Swiper spaceBetween={50} slidesPerView={1} align={"center"}>
      {services.map((service) => {
        return (
          <SwiperSlide>
            <img src={service.image} alt="servicePhoto" />
            <h3>{service.nameService}</h3>
            <p>{service.description}</p>
          </SwiperSlide>
        );
      })}
    </Swiper>
  );
};
