import { Link } from "react-router-dom";
import { AiOutlineMenu, AiOutlineClose } from "react-icons/ai";
import { useState } from "react";

const NavbarTop = () => {
  const [nav, setNav] = useState(true);
  const handleNav = () => {
    setNav(!nav);
  };

  return (
    <header className="bg-[#C1E8FF]">
      <nav className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex-shrink-0">
            <Link to="/" className="text-3xl font-bold text-[#060606]">
              MrBri<span className="text-[#1057E0]">Cool</span>
            </Link>
          </div>
          <div className="hidden md:flex md:items-center md:space-x-8">
            <Link to="/" className="text-blue-900 font-bold hover:underline">
              Devenir MrBriCool
            </Link>
            <Link
              to="/"
              className="text-white font-bold bg-[#1057E0] hover:bg-blue-800 rounded-3xl text-sm px-5 py-2.5 focus:outline-none"
            >
              Trouver un Bricoleur
            </Link>
            <div className="flex items-center space-x-4">
              <Link
                to="/"
                className="text-black bg-white hover:bg-gray-100 rounded-3xl text-sm px-5 py-2.5 focus:outline-none"
              >
                S'inscrire
              </Link>
              <Link
                to="home"
                className="text-black bg-white hover:bg-gray-100 rounded-3xl text-sm px-5 py-2.5 focus:outline-none"
              >
                Se Connecter
              </Link>
            </div>
          </div>
          <div className="flex md:hidden">
            <button
              onClick={handleNav}
              className="text-[#1057E0] hover:text-[#060606] focus:outline-none"
            >
              {!nav ? (
                <AiOutlineClose size={25} />
              ) : (
                <AiOutlineMenu size={25} />
              )}
            </button>
          </div>
        </div>
      </nav>
      <div
        className={`fixed inset-0 bg-[#338cf3] transform ${
          nav ? "-translate-x-full" : "translate-x-0"
        } ease-in-out duration-500 z-50`}
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <Link to="/" className="text-3xl font-bold text-black mb-8">
            MrBri<span className="text-[#1057E0]">Cool</span>
          </Link>
          <ul className="uppercase space-y-4">
            <li>
              <Link
                to="/"
                className="text-white hover:text-[#060606] font-bold text-sm"
              >
                Trouver un Bricoleur
              </Link>
            </li>
            <li>
              <Link
                to="/"
                className="text-white hover:text-[#060606] font-bold text-sm"
              >
                S'inscrire
              </Link>
            </li>
            <li>
              <Link
                to="home"
                className="text-white hover:text-[#060606] font-bold text-sm"
              >
                Se Connecter
              </Link>
            </li>
          </ul>
        </div>
      </div>
    </header>
  );
};

export default NavbarTop;

